#' @export
ttestIS <- function(
    calc = "n",
    es = 0.5,
    power = 0.8,
    n = 20,
    alt = "two.sided",
    alpha = 0.05,
    powerDist = FALSE,
    powerCurveES = TRUE,
    powerCurveN = FALSE) {

    options <- ttestISOptions$new(
        calc = calc,
        es = es,
        power = power,
        n = n,
        alt = alt,
        alpha = alpha,
        powerDist = powerDist,
        powerCurveES = powerCurveES,
        powerCurveN = powerCurveN)

    results <- ttestISResults$new(
        options = options)

    analysis <- ttestISClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
